%sh
cat <<EOF > /tmp/mysql.sql
set echo on
set pages 999
set timing on
REM connect to PDB
ALTER SESSION SET container = PDB1;
SHOW CON_NAME
REM
drop tablespace ts_temp;
create temporary tablespace ts_temp tempfile '/u02/app/oracle/oradata/ORCL/PDB1/ts_temp01.dbf' size 1G reuse autoextend on;
drop tablespace ts_data including contents and datafiles;
create tablespace ts_data datafile '/u02/app/oracle/oradata/ORCL/PDB1/ts_data01.dbf' size 2G autoextend on;
exit;
EOF

sqlplus / as sysdba @/tmp/mysql.sql

%sh
cat <<EOF > /tmp/mysql.sql
set echo on
set pages 999
set timing on
REM connect to PDB
ALTER SESSION SET container = PDB1;
SHOW CON_NAME
REM
drop user ssb cascade;
create user ssb identified by ssb default tablespace ts_data temporary tablespace ts_temp;
grant dba to ssb;
connect ssb/ssb@PDB1
create or replace directory expdumpdir1 as '/tmp';
exit;
EOF

sqlplus / as sysdba @/tmp/mysql.sql

%sh
cat <<EOF > /tmp/mysql.sql
set echo on
set pages 999
set timing on
alter system set memory_target=4608M scope=spfile;
alter system set memory_max_target=4608M scope=spfile;
alter system set sga_max_size=3584M scope=spfile;
alter system set inmemory_size=1536M scope=spfile;
startup force;
exit;
EOF

sqlplus / as sysdba @/tmp/mysql.sql

%sh
cat <<EOF > /tmp/mysql.sql
