set pages 999
set timing on
alter table PART cache;
alter table CUSTOMER cache;
alter table SUPPLIER cache;
alter table LINEORDER cache;
alter table DATE_DIM cache;
exit;
EOF

sqlplus ssb/ssb@PDB1 @/tmp/mysql.sql

%sh
