import org.apache.spark.sql.expressions.Window;

df2 = df2.withColumn( 
    "TIME_unix_time", 
    unix_timestamp(df2.col("time"), "yyyy-MM-dd'T'HH:mm:ss'Z'")
)
//df2.printSchema()

// Partitioning on <athlete> and <activity_type>
//var win = window.partitionBy(Seq("athlete", "activityType")).orderBy("TIME_unix_time")
var win = Window.partitionBy("athlete").orderBy("TIME_unix_time")

    // Time difference in seconds between tracking point and previous tracking point
    df2 = df2.withColumn(
        "TIME_unix_time_diff",
        df2.col("TIME_unix_time") - lag("TIME_unix_time", 1).over(win)
    )
    // Latitude and longditude in radians
    df2 = df2.withColumn( "DIST_latR", radians(df2.col("lat")) )
    df2 = df2.withColumn( "DIST_lonR", radians(df2.col("lon")) )

    // Latitude and longditude in previous tracking point
    df2 = df2.withColumn("DIST_latR_prev",lag("DIST_latR", 1).over(win) )
    df2 = df2.withColumn("DIST_lonR_prev",lag("DIST_lonR", 1).over(win) )

    // Difference in latitude and longditude since previous tracking point
    df2 = df2.withColumn("DIST_latR_diff", coalesce(df2.col("DIST_latR") - df2.col("DIST_latR_prev"), lit(0)) )
    df2 = df2.withColumn("DIST_lonR_diff", coalesce(df2.col("DIST_lonR") - df2.col("DIST_lonR_prev"), lit(0)) )

    // Havesine distance calculation between two tracking points
    df2 = df2.withColumn( "DIST_a",
        sin(df2.col("DIST_latR_diff")/2) * sin(df2.col("DIST_latR_diff")/2)
        + cos(df2.col("DIST_latR")) * cos(df2.col("DIST_latR_prev"))
        * sin(df2.col("DIST_lonR_diff")/2) * sin(df2.col("DIST_lonR_diff")/2)
    )

    df2 = df2.withColumn("DIST_c",
        expr("2 * atan2(sqrt(DIST_a), sqrt(1 - DIST_a))")
    )

     // Earth radius = 6371000 meters.  http://whatis.techtarget.com/definition/Earths-mean-radius

    // Distance between consecutive points
    df2 = df2.withColumn("DIST_diff_meters",
        coalesce(expr("6371000 * DIST_c"), lit(0))
    )

    // Momentary speed in km/h
    //3.6 is 60minutes*60seconds divided by 1000m per KM
    df2 = df2.withColumn("SPEED_kmh",
        coalesce(
            expr("3.6 * DIST_diff_meters / TIME_unix_time_diff"),
            lit(0)
        )
    )

    df2 = df2.withColumn("SPEED_mph",
      expr("SPEED_kmh * 0.621371"))

df2.printSchema()
df2.createOrReplaceTempView("activity_temp2")

